public class Practice1_Area {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//常數宣告
		final double PI = 3.1415926;
	
		//變數宣告
		double area;
	    double r = 20.0;
	    
	    //計算面積
	    area = PI*r*r;
	    
	    System.out.print("圓面積");
	    System.out.println(area);

	}

}
